package com.csi.star;

public class Rightangletriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("RIGHTANGLE TRIANGLE");
		for(int i=1;i<=4;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("* ");
			}
			System.out.println();
		}
		
		
		//oppoisite triangle
		System.out.println("downword RIGHTANGLE TRIANGLE");
		for(int i=1;i<=4;i++)
		{
			for(int j=4;j>=i;j--)
			{
				System.out.print("* ");
			}
			System.out.println();
		}
		System.out.println("adding pattern right angle tiangle");
		for(int i=1;i<=4;i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("* ");
			}
			System.out.println();
		}
		for(int i=1;i<=3;i++)
		{
			for(int j=3;j>=i;j--)
			{
				System.out.print("* ");
			}
			System.out.println();
		}
		
		
		
		
	}


}
